// MOBILE
$('.mobile i').hover(function() {
  $('.mobile h3 a').css('text-decoration', 'underline');
  $(this).css({'background': 'rgba(0,0,0,0.2)', 'color': '#829693'});
}, function() {
  $('.mobile h3 a').css('text-decoration', 'none');
  $(this).css('background', 'rgba(0,0,0, 0)');
  $(this).css('color', '#829693');
});

$('.mobile h3 a').hover(function() {
  $('.mobile i').css({'background': 'rgba(0,0,0,00.2)', 'color': '#829693'});
  $(this).css('text-decoration', 'underline');
}, function() {
  $('.mobile i').css({'background': 'rgba(0,0,0,0)', 'transition': '.4s', 'color': '#829693'});
  $(this).css('text-decoration', 'none');
});

