// MOBILE
$('.mobile i').hover(function() {
  $('.mobile h3 a').css('text-decoration', 'underline');
  $(this).css({'background': 'rgba(0,0,0,.1)', 'color': '#333'});
}, function() {
  $('.mobile h3 a').css('text-decoration', 'none');
  $(this).css('background', 'rgba(255,255,255,.25)');
  $(this).css('color', '#333');
});

$('.mobile h3 a').hover(function() {
  $('.mobile i').css({'background': 'rgba(0,0,0,.1)', 'color': '#333'});
  $(this).css('text-decoration', 'underline');
}, function() {
  $('.mobile i').css({'background': 'rgba(255,255,255,.25)', 'transition': '.4s', 'color': '#333'});
  $(this).css('text-decoration', 'none');
});

